import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import API from "../api";
import "./Auth.css";

function Profile() {
  const storedUser = JSON.parse(localStorage.getItem("user")); // email stored after login
  const [user, setUser] = useState(null);

  useEffect(() => {
    if (storedUser?.email) {
      API.get(`/auth/profile/${storedUser.email}`)
        .then((res) => setUser(res.data))
        .catch((err) => {
          console.error("Profile fetch error:", err);
          setUser(null);
        });
    }
  }, [storedUser]);

  if (!storedUser) {
    return (
      <div className="auth-container">
        <motion.div className="auth-box text-center" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <h2>No user logged in</h2>
        </motion.div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="auth-container">
        <motion.div className="auth-box text-center" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <h2>Loading profile...</h2>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="auth-container">
      <motion.div
        className="auth-box text-center"
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.8 }}
      >
        <div style={{ fontSize: "50px", marginBottom: "10px" }}>ðŸ‘¤</div>
        <h2 className="mb-4">Profile</h2>
        <hr style={{ borderColor: "rgba(255,255,255,0.3)" }} />

        <p><strong>ðŸ‘¤ Name:</strong> {user.name}</p>
        <p><strong>ðŸŽ‚ Age:</strong> {user.age || "N/A"}</p>
        <p><strong>ðŸ“… DOB:</strong> {user.dob ? new Date(user.dob).toDateString() : "N/A"}</p>
        <p><strong>ðŸ’¼ Occupation:</strong> {user.occupation || "N/A"}</p>
        <p><strong>ðŸŽ“ Education:</strong> {user.education || "N/A"}</p>
        <p><strong>ðŸ“§ Email:</strong> {user.email}</p>
      </motion.div>
    </div>
  );
}

export default Profile;
