import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api";
import "./Auth.css"; // reuse your styles

function Profile() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    API.get("/auth/profile", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => setUser(res.data))
      .catch(() => {
        localStorage.removeItem("token");
        navigate("/login");
      });
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (!user) return <h3 className="text-center mt-5">Loading profile...</h3>;

  return (
    <div className="auth-container">
      <motion.div
        className="auth-box"
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        style={{ textAlign: "left" }}
      >
        <h2 className="mb-3">ðŸ‘¤ Profile</h2>

        <div className="profile-details">
          <p><b>Name:</b> {user.name}</p>
          <p><b>Email:</b> {user.email}</p>
          <p><b>Age:</b> {user.age || "N/A"}</p>
          <p><b>DOB:</b> {user.dob || "N/A"}</p>
          <p><b>Occupation:</b> {user.occupation || "N/A"}</p>
          <p><b>Education:</b> {user.education || "N/A"}</p>
        </div>

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="btn-gradient mt-3"
          onClick={handleLogout}
        >
          Logout
        </motion.button>
      </motion.div>
    </div>
  );
}

export default Profile;
