import React, { useEffect, useState } from "react";
import API from "../api";

function Profile() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      API.get("/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => setUser(res.data))
        .catch((err) => {
          console.error("Profile error:", err.response?.data);
          setUser(null);
        });
    }
  }, []);

  if (!user) return <h3>Loading profile...</h3>;

  return (
    <div className="profile-container">
      <h2>Profile</h2>
      <p><b>Name:</b> {user.name}</p>
      <p><b>Email:</b> {user.email}</p>
      <p><b>Age:</b> {user.age || "N/A"}</p>
      <p><b>DOB:</b> {user.dob || "N/A"}</p>
      <p><b>Occupation:</b> {user.occupation || "N/A"}</p>
      <p><b>Education:</b> {user.education || "N/A"}</p>
    </div>
  );
}

export default Profile;
