import React, { useState } from "react";
import API from "../api";
import "./Auth.css";

function Register() {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    dob: "",
    occupation: "",
    education: "",
    email: "",
    password: "",
  });

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.post("/auth/register", formData);
      alert("Registration successful! Please login.");
      window.location.href = "/login";
    } catch (err) {
      alert(err.response?.data?.msg || "Registration failed");
    }
  };

  return (
    <div className="auth-container">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Name" onChange={handleChange} className="form-control mb-2" />
        <input name="age" placeholder="Age" onChange={handleChange} className="form-control mb-2" />
        <input name="dob" placeholder="Date of Birth" onChange={handleChange} className="form-control mb-2" />
        <input name="occupation" placeholder="Occupation" onChange={handleChange} className="form-control mb-2" />
        <input name="education" placeholder="Education" onChange={handleChange} className="form-control mb-2" />
        <input name="email" type="email" placeholder="Email" onChange={handleChange} className="form-control mb-2" />
        <input name="password" type="password" placeholder="Password" onChange={handleChange} className="form-control mb-2" />
        <button type="submit" className="btn btn-success w-100">Register</button>
      </form>

      <p>
        Already have an account? <a href="/login">Login</a>
      </p>
    </div>
  );
}

export default Register;
