import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api";
import "./Auth.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post("/auth/login", { email, password });
      setMessage(res.data.msg);

      if (res.data.user) {
        localStorage.setItem("user", JSON.stringify(res.data.user)); // ✅ Save user
        navigate("/dashboard");
      }
    } catch (err) {
      setMessage(err.response?.data?.msg || "Error occurred");
    }
  };

  return (
    <div className="auth-container">
      <motion.div className="auth-box" initial={{ opacity: 0, y: -40 }} animate={{ opacity: 1, y: 0 }}>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input type="email" placeholder="Email" value={email}
            onChange={(e) => setEmail(e.target.value)} className="form-control" />
          <input type="password" placeholder="Password" value={password}
            onChange={(e) => setPassword(e.target.value)} className="form-control" />
          <motion.button className="btn-gradient" whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
            Login
          </motion.button>
        </form>
        {message && <p className="mt-3">{message}</p>}
      </motion.div>
    </div>
  );
}

export default Login;
