import React from "react";
import { motion } from "framer-motion";
import "./Auth.css";

function Profile() {
  const user = JSON.parse(localStorage.getItem("user"));

  if (!user) {
    return (
      <div className="auth-container">
        <motion.div className="auth-box text-center" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <h2>No user logged in</h2>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="auth-container">
      <motion.div className="auth-box"
        initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <h2 className="text-center mb-4">ðŸ‘¤ Profile</h2>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Age:</strong> {user.age}</p>
        <p><strong>Date of Birth:</strong> {user.dob ? new Date(user.dob).toDateString() : "N/A"}</p>
        <p><strong>Occupation:</strong> {user.occupation}</p>
        <p><strong>Education:</strong> {user.education}</p>
        <p><strong>Email:</strong> {user.email}</p>
      </motion.div>
    </div>
  );
}

export default Profile;
